package com.baseui.customview.activity;import android.os.Bundle;import android.view.View;import android.view.animation.Animation;import android.view.animation.DecelerateInterpolator;import android.widget.ImageView;import com.baseui.customview.R;import com.baseui.customview.view.animation.Rotate3dAnimation;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;public class Rotate3DActivity extends AppCompatActivity {    private View mContentRoot;    private int duration = 600;    private Rotate3dAnimation openAnimation;    private Rotate3dAnimation closeAnimation;    private boolean isOpen = false;    private View mBottom_open;    private ImageView imageView_logo;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_rotate_3d);        mContentRoot = findViewById(R.id.iv_content_Rotate3DActivity);        mBottom_open = findViewById(R.id.btn_open_Rotate3DActivity);        imageView_logo = findViewById(R.id.iv_logo);        initOpenAnim();        initCloseAnim();    }    private void initOpenAnim() {        openAnimation =new Rotate3dAnimation(0,90,true);        openAnimation.setDuration(duration);        openAnimation.setFillAfter(true);        openAnimation.setAnimationListener(new Animation.AnimationListener() {            @Override            public void onAnimationStart(Animation animation) {            }            @Override            public void onAnimationEnd(Animation animation) {                imageView_logo.setImageResource(R.drawable.girl);                Rotate3dAnimation rotate3dAnimation = new Rotate3dAnimation(90,180,false);                rotate3dAnimation.setDuration(duration);                rotate3dAnimation.setFillAfter(true);                rotate3dAnimation.setInterpolator(new DecelerateInterpolator());                mContentRoot.startAnimation(rotate3dAnimation);            }            @Override            public void onAnimationRepeat(Animation animation) {            }        });    }    private void initCloseAnim() {        closeAnimation =new Rotate3dAnimation(180,90,true);        closeAnimation.setDuration(duration);        closeAnimation.setFillAfter(true);        closeAnimation.setAnimationListener(new Animation.AnimationListener() {            @Override            public void onAnimationStart(Animation animation) {            }            @Override            public void onAnimationEnd(Animation animation) {                Rotate3dAnimation rotate3dAnimation = new Rotate3dAnimation(90,0,false);                rotate3dAnimation.setDuration(duration);                rotate3dAnimation.setFillAfter(true);                rotate3dAnimation.setInterpolator(new DecelerateInterpolator());                mContentRoot.startAnimation(rotate3dAnimation);            }            @Override            public void onAnimationRepeat(Animation animation) {            }        });    }    public void onClickView(View view) {        if (openAnimation.hasStarted() && !openAnimation.hasEnded()) {            return;        }        if(closeAnimation.hasStarted() && ! openAnimation.hasEnded()) {            return;        }        if(isOpen){            mContentRoot.startAnimation(closeAnimation);        }else {            mContentRoot.startAnimation(openAnimation);        }        isOpen = !isOpen;    }}