package com.baseui.customview.activity;import android.os.Bundle;import android.widget.SeekBar;import android.widget.TextView;import com.baseui.customview.R;import com.baseui.customview.view.CameraImageView;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;public class TranslateActivity extends AppCompatActivity{    private SeekBar mSeekBar;    private CameraImageView mCameraImageView;    private TextView mTv;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_translate);        mCameraImageView = findViewById(R.id.camera_img);        mTv = findViewById(R.id.tv_progress);        mSeekBar = findViewById(R.id.btn_progress);        mSeekBar.setOnSeekBarChangeListener(new SeekBar.OnSeekBarChangeListener() {            @Override            public void onProgressChanged(SeekBar seekBar, int progress, boolean fromUser) {                mCameraImageView.setProgress(progress);                mTv.setText(progress + "");            }            @Override            public void onStartTrackingTouch(SeekBar seekBar) {            }            @Override            public void onStopTrackingTouch(SeekBar seekBar) {            }        });    }}